import React, {Component} from 'react'
import StructureStoreDisplay from './StructureStoreDisplay'

class StructureStore extends Component {
  state = {
    fishPole: {
      price: 50,
      cps: 0.5,
      ammount: 0
    },
    net: {
      price: 300,
      cps: 2,
      ammount: 0
    },
    boat: {
      price: 2500,
      cps: 15,
      ammount: 0
    },
    crew: {
      price: 21000,
      cps: 120,
      ammount: 0
    },
    yacht: {
      price: 180000,
      cps: 900,
      ammount: 0
    },
    factory: {
      price: 1000000,
      cps: 8000,
      ammount: 0
    },
    portal: {
      price: 10000000,
      cps: 70000,
      ammount: 0
    }
  }

  handleClick = (event) => {
    const { name } = event.target

    this.setState(prevState => {
      return{
          [name]: {
            ammount: prevState[name].ammount + 1
          }
      }
    })
    console.log(this.state[name])
  }

  render() {
    return (
      <StructureStoreDisplay state={this.state} handleClick={this.handleClick} />
    )
  }
}

export default StructureStore
