import React, {Component} from 'react'
import StructureStoreDisplay from './StructureStoreDisplay'

class StructureStore extends Component { 
  state = {
    fishPole: {
      price: 50,
      fps: 0.5,
      ammount: 0
    },
    net: {
      price: 300,
      fps: 2,
      ammount: 0
    },
    boat: {
      price: 2500,
      fps: 15,
      ammount: 0
    },
    crew: {
      price: 21000,
      fps: 120,
      ammount: 0
    },
    yacht: {
      price: 180000,
      fps: 900,
      ammount: 0
    },
    factory: {
      price: 1000000,
      fps: 8000,
      ammount: 0
    },
    portal: {
      price: 10000000,
      fps: 70000,
      ammount: 0
    }
  }

  static contextType = fishContext

  handleClick = (event) => {
    const { handleStructureBuy } = this.context

    const { name } = event.target

    if (this.props.fishCount >= this.state[name].price)
    {
      this.setState(prevState => {
        return{
          [name]: {
            price: prevState[name].price * 1.2,
            fps: prevState[name].fps,
            ammount: prevState[name].ammount + 1
          }
        }
      })
    }
  }

  test = () => {
    console.log(this.state)
  }

  render() {
    return (
      <div>
        <StructureStoreDisplay state={this.state} handleClick={this.handleClick} />
        <button onClick={this.test}> Test </button>
      </div>
    )
  }
}

export default StructureStore
