import React, {Component} from 'react'
import StructureStoreDisplay from './StructureStoreDisplay'
import { FishContext } from '../FishContext'

class StructureStore extends Component { 
  state = {
    fishPole: {
      price: 50,
      fps: 0.5,
      ammount: 0
    },
    net: {
      price: 300,
      fps: 2,
      ammount: 0
    },
    boat: {
      price: 2500,
      fps: 15,
      ammount: 0
    },
    crew: {
      price: 21000,
      fps: 120,
      ammount: 0
    },
    yacht: {
      price: 180000,
      fps: 900,
      ammount: 0
    },
    factory: {
      price: 1000000,
      fps: 8000,
      ammount: 0
    },
    portal: {
      price: 10000000,
      fps: 70000,
      ammount: 0
    }
  }

  static contextType = FishContext

  componentDidMount() {
    const { handleFishUpdate } = this.context

    this.interval = setInterval(() => handleFishUpdate(this.state), 200)
  }

  // componentDidUpdate() {
  //   if (typeof getStructures !=== "function") {
  //     const { getStructures } = this.context
  //   }
  //   getStructures(this.state)
  // }

  // Im having an issue where the first buy doesnt result in a getStructures being called after the buy. I think its firing first
  handleClick = (event) => {
    const { getStructures, handleStructureBuy, fishCount } = this.context
    const { name } = event.target

    handleStructureBuy(this.state[name])

    if ( fishCount >= this.state[name].price ) {
      this.setState( prevState => {
        return { [name]: {
          price: prevState[name].price * 1.2,
          fps: prevState[name].fps,
          ammount: prevState[name].ammount + 1
        }}
      })
    }

    this.updateContext()

  }

  test = () => {
    console.log(this.state)
  }

  render() {
    return (
      <div>
        <StructureStoreDisplay state={this.state} handleClick={this.handleClick} />
        <button onClick={this.test}> Test </button>
      </div>
    )
  }
}

export default StructureStore
